var express = require("express");
const { MongoClient } = require("mongodb");
const cors = require("cors");

client = new MongoClient(
  "mongodb://127.0.0.1:27017/FashionData?retryWrites=true&w=majority"
);
client.connect();
database = client.db("FashionData");

var router = express.Router();

router.use(cors());

router.use((req, res, next) => {
  res.setHeader("Access-Control-Allow-Origin", "*");
  res.setHeader(
    "Access-Control-Allow-Methods",
    "GET, POST, PUT, OPTIONS, DELETE"
  );
  res.setHeader("Access-Control-Allow-Headers", "Content-Type, Authorization");
  next();
});

const response = (code, data, total = "") => {
  let message;
  if (code != 200) {
    message = "not ok";
  } else {
    message = "ok";
  }
  if (total) {
    return {
      code: code,
      message: message,
      total: total,
      data: data,
    };
  }
  return {
    code: code,
    message: message,
    data: data,
  };
};

router.get("/fashions/list", cors(), async (req, res) => {
  const result = await database.collection("Fashion").find({}).toArray();
  res.send(response(200, result));
});

router.post("/uploadsfile", (req, res) => {
  res.send(response(200, ""));
});

router.get("/blogs/list", async (req, res) => {
  const page = req.query.page - 1;
  const limit = req.query.limit;
  const count = await database.collection("Blogs").count();
  const result = await database
    .collection("Blogs")
    .find({})
    .skip(Number(page))
    .limit(Number(limit))
    .toArray();
  res.send(response(200, result, count));
});

router.get("/blogs/detail/:id", async (req, res) => {
  try {
    var ObjectId = require("mongodb").ObjectId;
    const id = req.params.id;
    var o_id = new ObjectId(id);
    const con = { _id: o_id };
    const result = await database.collection("Blogs").findOne(con);
    res.send(response(200, result));
  } catch (error) {
    res.send(response(400, error));
  }
});

